function updateTime() {
  // Updating the time if the countdown has started
  if (countdownStarted && !showAlert) {
    if (seconds > 0) {
      seconds--;
    } else if (minutes > 0) {
      seconds = 59;
      minutes--;
    } else if (hours > 0) {
      minutes = 59;
      seconds = 59;
      hours--;
